document.addEventListener('DOMContentLoaded', () => {
  init();
});

let currentMonk = null;
let isAudioPlaying = false;
let player;

function init() {
  // Load YouTube IFrame API
  var tag = document.createElement('script');
  tag.src = "https://www.youtube.com/iframe_api";
  var firstScriptTag = document.getElementsByTagName('script')[0];
  firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

  // Simulate loading
  const loadingScreen = document.getElementById('loading-screen');
  const mainContent = document.getElementById('main-content');

  setTimeout(() => {
    selectRandomMonk();

    loadingScreen.style.opacity = '0';
    loadingScreen.style.transition = 'opacity 0.5s ease-out';

    setTimeout(() => {
      loadingScreen.classList.add('hidden');
      loadingScreen.classList.remove('active');
      mainContent.classList.remove('hidden');
    }, 500);

  }, 2500);

  setupEventListeners();
}

// YouTube API Ready Function
function onYouTubeIframeAPIReady() {
  player = new YT.Player('youtube-player', {
    height: '0',
    width: '0',
    videoId: 'Ut9Xjq_4__A', // New BGM
    playerVars: {
      'playsinline': 1,
      'autoplay': 1,
      'controls': 0,
      'loop': 1,
      'playlist': 'Ut9Xjq_4__A',
      'start': 7
    },
    events: {
      'onReady': (event) => {
        event.target.playVideo();
        event.target.unMute();
        isAudioPlaying = true;

        // Update UI
        const iconOff = document.getElementById('icon-audio-off');
        const iconOn = document.getElementById('icon-audio-on');
        if (iconOff && iconOn) {
          iconOff.classList.add('hidden');
          iconOn.classList.remove('hidden');
        }

        // Browser Autoplay Policy Workaround: Unmute on first interaction
        const unmuteOnInteraction = () => {
          if (player && player.unMute) {
            player.unMute();
            if (player.getPlayerState() !== 1) { // If not playing
              player.playVideo();
            }
          }
          document.removeEventListener('click', unmuteOnInteraction);
          document.removeEventListener('touchstart', unmuteOnInteraction);
          document.removeEventListener('keydown', unmuteOnInteraction);
        };

        document.addEventListener('click', unmuteOnInteraction);
        document.addEventListener('touchstart', unmuteOnInteraction);
        document.addEventListener('keydown', unmuteOnInteraction);
      }
    }
  });
}

function selectRandomMonk() {
  // Default to Manhae (ENFP) as requested
  const manhae = monksData.find(m => m.id === 'ENFP');
  currentMonk = manhae || monksData[0]; // Fallback if not found

  renderMonk(currentMonk);
}

function renderMonk(monk) {
  const cardContainer = document.querySelector('.monk-card');

  // Clear existing content
  cardContainer.innerHTML = '';

  // === 1. HEADER ===
  const header = document.createElement('div');
  header.className = 'card-header-text';
  header.textContent = `SUPER MONK BROS. - ${monk.id}`;
  cardContainer.appendChild(header);

  // === 2. IMAGE WRAPPER (CRT Screen) ===
  const imageWrapper = document.createElement('div');
  imageWrapper.className = 'card-image-wrapper';

  const imgElement = document.createElement('img');
  imgElement.id = 'monk-image';
  imgElement.className = 'card-image';
  imgElement.alt = `${monk.name} Illustration`;

  if (monk.image) {
    imgElement.src = monk.image;
  } else {
    // Fallback logic
    const tanghwaImages = ['tanghwa_monk_1.png', 'tanghwa_monk_2.png', 'tanghwa_monk_3.png'];
    const imageIndex = monk.name.charCodeAt(0) % tanghwaImages.length;
    imgElement.src = tanghwaImages[imageIndex];
  }

  imageWrapper.appendChild(imgElement);

  // Audio Button (Green Pixel Style) - Top Right
  const audioBtn = document.createElement('button');
  audioBtn.id = 'btn-audio-pixel';
  audioBtn.className = 'btn-audio-pixel';
  audioBtn.textContent = 'BGM ON'; // Initial text

  audioBtn.addEventListener('click', (e) => {
    e.stopPropagation(); // Prevent bubbling
    isAudioPlaying = !isAudioPlaying;

    if (isAudioPlaying) {
      audioBtn.textContent = 'BGM ON';
      audioBtn.classList.remove('muted');
      if (player && player.playVideo) {
        player.unMute();
        player.playVideo();
      }
    } else {
      audioBtn.textContent = 'BGM OFF';
      audioBtn.classList.add('muted');
      if (player && player.pauseVideo) {
        player.pauseVideo(); // Or mute? User asked for auto play, maybe just mute is better but pause is fine.
      }
    }
  });

  imageWrapper.appendChild(audioBtn);

  // Envelope Button inside Image Wrapper
  const envelopeBtn = document.createElement('button');
  envelopeBtn.id = 'btn-story-envelope';
  envelopeBtn.className = 'envelope-button';
  envelopeBtn.setAttribute('aria-label', 'Read Story');

  envelopeBtn.addEventListener('click', () => {
    envelopeBtn.classList.add('open');
    setTimeout(() => {
      const modal = document.getElementById('story-modal');
      modal.classList.remove('hidden');
      envelopeBtn.classList.remove('open');
    }, 600);
  });

  imageWrapper.appendChild(envelopeBtn);
  cardContainer.appendChild(imageWrapper);


  // === 3. PROFILE INFO (Moved Up, No Footer) ===
  const profileInfo = document.createElement('div');
  profileInfo.className = 'profile-info';

  // MBTI Pill
  const mbtiPill = document.createElement('span');
  mbtiPill.className = 'mbti-pill';
  mbtiPill.textContent = monk.id;
  profileInfo.appendChild(mbtiPill);

  // Name
  const nameEl = document.createElement('h2');
  nameEl.className = 'profile-name';
  nameEl.textContent = monk.name;
  profileInfo.appendChild(nameEl);

  // Subtitle (Catchphrase)
  const subtitleEl = document.createElement('p');
  subtitleEl.className = 'profile-subtitle';
  subtitleEl.textContent = monk.catchphrase;
  profileInfo.appendChild(subtitleEl);

  // Divider
  const divider = document.createElement('div');
  divider.className = 'profile-divider';
  profileInfo.appendChild(divider);

  // Quote
  const quoteEl = document.createElement('p');
  quoteEl.className = 'profile-quote';
  quoteEl.textContent = `"${monk.quote}"`;
  profileInfo.appendChild(quoteEl);

  // Append Profile Info to Main Container
  cardContainer.appendChild(profileInfo);

  // Update Modal Content
  document.getElementById('modal-title').textContent = monk.name;
  document.getElementById('modal-text').innerHTML = monk.content;
}

function setupEventListeners() {
  // Modal
  // Modal
  const btnStory = document.getElementById('btn-story-envelope');
  const modal = document.getElementById('story-modal');
  const btnCloseModal = document.getElementById('btn-close-modal');
  const modalOverlay = document.querySelector('.modal-overlay');

  btnStory.addEventListener('click', () => {
    // Play animation
    btnStory.classList.add('open');

    // Wait for animation then open modal
    setTimeout(() => {
      modal.classList.remove('hidden');
    }, 600);
  });

  const closeModal = () => {
    modal.classList.add('hidden');
    // Reset envelope
    setTimeout(() => {
      btnStory.classList.remove('open');
    }, 300);
  };

  btnCloseModal.addEventListener('click', closeModal);
  modalOverlay.addEventListener('click', closeModal);


  // Audio
  const btnAudio = document.getElementById('btn-audio');
  const iconOff = document.getElementById('icon-audio-off');
  const iconOn = document.getElementById('icon-audio-on');

  btnAudio.addEventListener('click', () => {
    isAudioPlaying = !isAudioPlaying;

    if (isAudioPlaying) {
      iconOff.classList.add('hidden');
      iconOn.classList.remove('hidden');
      if (player && player.playVideo) {
        player.unMute(); // Ensure audio is unmuted
        player.playVideo();
      }
    } else {
      iconOff.classList.remove('hidden');
      iconOn.classList.add('hidden');
      if (player && player.pauseVideo) {
        player.pauseVideo();
      }
    }
  });
}
